const sampleQuiz = [
    {
        moveNumber: 1,
        white: "e4",
        black: "e5",
    },
    {
        moveNumber: 2,
        white: "Nf3",
        black: "Nc6",
    },
    {
        moveNumber: 3,
        white: "Bb5",
        black: "a6",
    },
    {
        moveNumber: 4,
        white: "Ba4",
        black: "Nf6",
    },
]

export function isMoveCorrect(userMove, moveNum, color) {
    const correctMove = getMovesFromPGN(moveNum);
    console.log(`User Move: ${userMove}, Correct Move: ${correctMove}, Color: ${color}`);

    return userMove === correctMove;
}

export function getMovesFromPGN(moveNum) {
    return (sampleQuiz[moveNum - 1]);
}
