const sampleQuiz = [
    {
        moveNumber: 1,
        white: "e4",
        black: "e5",
    },
    {
        moveNumber: 2,
        white: "Nf3",
        black: "Nc6",
    },
    {
        moveNumber: 3,
        white: "Bb5",
        black: "a6",
    },
    {
        moveNumber: 4,
        white: "Ba4",
        black: "Nf6",
    },
]

export function isMoveCorrect(userMove, moveNum, color) {
    const correctMove = getMovesFromPGN(moveNum)[color];
    if (!correctMove) {
        console.error(`No move found for move number ${moveNum} and color ${color}`);
        return false;
    }
    // For now, we will just return true if the userMove matches the correctMove
    // In a real application, you might want to implement more complex logic
    // to validate the move, such as checking if the move is legal in the context of
    // the current game state, or if it matches the expected move notation.
    console.log(`User move: ${userMove}, Correct move: ${correctMove}`);

    return; //userMove === correctMove;
}

export function getMovesFromPGN(moveNum) {
    return (sampleQuiz[moveNum - 1]);
}
